import React, { useContext, useReducer, useState } from 'react';
import { Helmet } from 'react-helmet-async';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import { Store } from '../Store';
import { cssTransition, toast } from 'react-toastify';
import { getError } from '../utils';
import axios from 'axios';

const reducer = (state, action) => {
  switch (action.type) {
    case 'FETCH_DATA':
      return { ...state, bookings: action.payload, loading: false };
    default:
      return state;
  }
};

export default function MyBookingScreen() {
  const { state, dispatch: ctxDispatch } = useContext(Store);
  const { userInfo } = state;
  const [name, setName] = useState(userInfo.name);
  const [email, setEmail] = useState(userInfo.email);
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');

  const [{ bookings }, dispatch] = useReducer(reducer, {
    loading: false,
  });

  const submitHandler = async (e) => {
    e.preventDefault();
    try {
      const { data } = await axios.put(
        '/api/booking/me',
        {
          headers: { Authorization: `Bearer ${userInfo.token}` },
        }
      );
      dispatch({
        type: 'FETCH_DATA',
        data
      });
    } catch (err) {
      toast.error(getError(err));
    }
  };

  return (
    <div className="container small-container">
      <Helmet>
        <title>My Bookings</title>
      </Helmet>
      <h1 className="my-3">My Bookings</h1>
      <table className="table">
            <thead>
              <tr>
                <th>Service Name</th>
                <th>Service Address</th>
                <th>User Name</th>
                <th>User Address</th>
                <th>User Contact</th>
                <th>Booking Date/Time</th>
                <th>ACTIONS</th>
              </tr>
            </thead>
            <tbody>
              {bookings?.map((book) => (
                <tr key={book?._id}>
                  <td>{book?.businessid?.name}</td>
                  <td>{book?.businessid?.address}</td>
                  <td>{book?.userId?.name}</td>
                  <td>{book?.address}</td>
                  <td>{book?.mobile}</td>
                  <td>
                    {new Date(book?.date).toDateString() + "-" + book.time}
                  </td>

                  <td>
                    <Button
                      type="button"
                      variant="light"
                      onClick={() => navigate(`/admin/book/${book._id}`)}
                    >
                      Edit
                    </Button>
                    &nbsp;
                    <Button
                      type="button"
                      variant="light"
                      // onClick={() => deleteHandler(book)}
                    >
                      Delete
                    </Button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
    </div>
  );
}
